import React, { useState } from "react";

function Login() {
  const [creadentials, setCreadentials] = useState({ email: "", password: "" });
  let history = useHistory
  const host = `http://localhost:3001`;
  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = `${host}/loginUser`;
    const response = await fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email:creadentials.email, password:creadentials.password}),
    });

    const json = await response.json();
    console.log(json);
    if(json.status){
        localStorage.setItem('token', json.data.token)
    }else{
        alert("Invalid Creadentials")
    }
  };

  const handleOnchange = (e) => {
    setCreadentials({ ...creadentials, [e.target.name]: e.target.value });
  };
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">
            Email address
          </label>
          <input
            type="email"
            className="form-control"
            value={creadentials.email}
            onChange={handleOnchange}
            id="email"
            name="email"
            aria-describedby="emailHelp"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            value={creadentials.password}
            onChange={handleOnchange}
            id="password"
            name="password"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Login;
