import noteContext from "./noteContext";

const NoteState = (props) => {
    const host = "http://localhost:3001"
    const notes1 = []
    const [notes, setNotes] = useState(notes1)
    // const s1 = {
    //     "name": "Dipen",
    //     "class": "5b"
    // }
    // const [state, setState] = useState(s1);

    // const update = () => {
