    //         setState({
    //            "name":"pradip",
    //            "class":"B.tech-4year"
    //         })
    //     }, 1000);
    // }

    // -----------------Add a Note--------------

    const addNotes = async (title, description, tag) => {
        const url = `${host}/createNotes`
        const response = await fetch(url, {
            method: "PUT",
            headers: {
                'Content-Type': 'application/json'
            },
            auth: {
                "Bearer": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjQwMzY1NmM2YTE3YmZkZDhhZGFmMjg4In0sImF0IjoxNjc4MDg0ODE5LCJleHAiOjE2NzgxNzEyMTksImlhdCI6MTY3ODA4NDgxOX0.ArSVT1eyC09j1VczKH_RMqMMommdxPxWyqLN05hmbvY"
            },
            body: JSON.stringify({ title, description, tag })
        })


        let note = {
            // Todo:Api call
            "_id": "64036794130843d26409e870b",
            "user": "6403656c6a17bfdd8adaf288",
            "title": title,
            "description": description,
            "tag": tag,
            "isDeleted": false,
            "deletedAt": null,
            "date": "2023-03-04T15:45:24.139Z",
            "__v": 0
        }
        setNotes(notes.concat(note))
    }


    // --------------------- fetch all notes ---------------------

    const getNotes = async () => {
        const url = `${host}/getNotes`
        const response = await fetch(url, {
            method: "GET",
            headers: {
                'Content-Type': 'application/json'
            },
            auth: {
                "Bearer": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjQwMzY1NmM2YTE3YmZkZDhhZGFmMjg4In0sImF0IjoxNjc4MDg0ODE5LCJleHAiOjE2NzgxNzEyMTksImlhdCI6MTY3ODA4NDgxOX0.ArSVT1eyC09j1VczKH_RMqMMommdxPxWyqLN05hmbvY"
            },
        })
        const json = await response.json()
        console.log(json);
        setNotes(json)
    }

    // -----------------Edit a Note-----------------------------

    const editNotes = async (id, title, description, tag) => {
        const url = `${host}/updateNotes/${id}`
        const response = await fetch(url, {
            method: "PUT",
            headers: {
                'Content-Type': 'application/json',
                'Authorization':{
                    'Bearer Token':'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjQwMzY1NmM2YTE3YmZkZDhhZGFmMjg4In0sImF0IjoxNjc4MDg0ODE5LCJleHAiOjE2NzgxNzEyMTksImlhdCI6MTY3ODA4NDgxOX0.ArSVT1eyC09j1VczKH_RMqMMommdxPxWyqLN05hmbvY'
                }
            },

            body: JSON.stringify({ title, description, tag })
        })

        await response.json()

        // Logic to edit client

        for (let index = 0; index < notes.length; index++) {
            const element = notes[index];
            if (element._id === id) {
                element.title = title
                element.description = description
                element.tag = tag
            }
        }
    }

    // -----------------Delete a Note---------------

    const deleteNotes = (id) => {
        // Todo:Api call
        const newNotes = notes.filter((note) => { return note._id !== id })
        setNotes(newNotes)
    }


    return (
        // <noteContext.Provider value={{state:state, update:update}}>
        //     {props.children}
        // </noteContext.Provider>
        <noteContext.Provider value={{ notes, addNotes, getNotes, editNotes, deleteNotes }}>
            {props.children}
        </noteContext.Provider>
    )
}


export default NoteState;
